css:
.container {
  width: 100%;
  font-family: "Times New Roman", Times, serif;
  text-align: justify;
}

.logo{
    display: flex;
    justify-content: center;
    align-items: center;
}
.logo img {
  width: 200px; /* adjust as needed */
  height: auto;
}

.title {
    font-weight: bold;
    text-align: center;
}

.subtitle{
text-align: center;

}

.daytitle{
    font-weight: bold;
}

.dayimg{
    width: 100%;
    height: auto;
}
.description{
    text-align: justify;
}

.accomodation{
    text-align: end;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="logo">
    <img src="/files/zlogo.png" alt="" />
</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="margin-top: 5px;">
        <p class="title">Central Mongolia 2026</p>
        <p class="subtitle">
          {{ doc.duration }}
          {% if doc.tour_nights %} Days
            •
            {% for item in doc.tour_nights %}
              {{ item.night_description }}{% if not loop.last %} • {% endif %}
            {% endfor %}
          {% endif %}
        </p>
        <p class="subtitle">
            {% if doc.category %} {% for cat in doc.category %} {{ cat.category
            }}{% if not loop.last %} , {% endif %} {% endfor %} {% endif %}
          </p>
        <p class="subtitle">
    Tour grade: {% if doc.grades %} {% for grade in doc.grades %} {{
    grade.grade }}{% if not loop.last %} , {% endif %} {% endfor %} {% endif %}
  </p>
          {% for day in range(1, doc.duration + 1) %}
  {% set acc = (doc.accomodation | selectattr("day", "equalto", day) | list | first) %}
  {% set image = (doc.images | selectattr("day", "equalto", day) | list | first) %}

  {% if acc %}
    <div class="daytitle">
      Day {{ day }}. {{ acc.title }}
    </div>

    {% if image %}
      <img src="{{ image.attach_image_nssn }}" alt="" class="dayimg" />
      {% if image.title %}
        <div>Photo: {{ image.title }}</div>
      {% endif %}
    {% endif %}

    <p class="description">
      {{ acc.description }}
    </p>

    <div class="accomodation">
      ({{ acc.accomodation }}
      {% if acc.hotel_star and acc.hotel_star > 0 %}
        {{ "*" * acc.hotel_star }}
      {% endif %}
      {% set meals = [] %}
      {% if acc.breakfast %}{% set _ = meals.append('B') %}{% endif %}
      {% if acc.lunch %}{% set _ = meals.append('L') %}{% endif %}
      {% if acc.dinner %}{% set _ = meals.append('D') %}{% endif %}
      {% if meals|length > 0 %}
        ; {{ meals|join('') }}
      {% endif %}
      )
    </div>
  {% endif %}
{% endfor %}
      </div>"}]
